<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed'); class User{	private $data;	private $ci;	//getter / setters//	public function title(){//		if(func_num_args()==0) return $this->data['title'];//		else $this->data['title'] = func_get_arg(0);//	 }			 public function Data(){ //args(array) or Data(key, value);	 	if(func_num_args()==0) return $this->data;		else if(func_num_args()==1) { $t=func_get_arg(0); $this->data=array_merge($this->data,$t);}		else if(func_num_args()==2) $this->data[func_get_arg(0)]=func_get_arg(1);	 }	          function __construct($id=NULL) {            $this->ci = &get_instance();            $this->ci->load->library('session');                        $this->data = $this->ci->session->userdata('user');        } // end __construct()	        public function login($pass=NULL){            if($pass=='666'){                $user = array('verified' => TRUE);                $this->ci->session->set_userdata('user',$user);            }                    } // end login()                public function logout(){            $this->ci->session->unset_userdata('user');            header('Location: '.base_url('admin'));            exit();        } // end logout()        	 // return filename to capitalized text	 private function clean_title($input){	 	$input = preg_replace('/^(\w)|_(\w)/e', ' strtoupper("$0")', $input);	 	return preg_replace('/_/', ' ', $input);	 } // end clean_title() }